mapper.select:
select *, case when update_time > create_time then update_time else create_time end sortTime from t_orders
ORGAN_ID = #{organ_id,jdbcType=BIGINT}
order by sortTime desc

mapper.update:
update t_orders
UPDATEER_NAME = #{updateer_name,jdbcType=VARCHAR}, UPDATE_TIME = now()
where ORDER_ID = #{order_id} and ORGAN_ID = #{organ_id,jdbcType=BIGINT}
